document.addEventListener("alpine:initializing",()=>{window.Alpine.data("pageManager",()=>({init(){window.addEventListener("filament-page-manager::copy-url",e=>{this.copyUrl(e.detail.url)}),window.addEventListener("filament-page-manager::preview",e=>{window.open(e.detail.url,"_blank")})},copyUrl(e){window.navigator.clipboard.writeText(e),this.$tooltip("Copied",{theme:window.Alpine.store("theme"),timeout:2e3})}}))});
